 		calculate=MPTinR2.js.calc,
 		printout=MPTinR2.js.print),
	pluginmap=list(name="MPTinR2", hierarchy=list("analysis", "MPT")),
	create=c("pmap", "xml", "js", "desc"),
	overwrite=overwrite,
	tests=FALSE,
#	edit=TRUE,
	load=TRUE)#,
#	show=TRUE)
})